#[test]
fn default_value_deserialization() {
    let p = Proof::default();
    let bytes = p.to_octets();
    let _ = Proof::from_octets(&bytes)
        .expect_err("default value deserialization should fail");
}

#[test]
fn gen_verify_serde_nominal() {
    const NUM_MESSAGES: usize = 5;
    let messages = get_random_test_messages(NUM_MESSAGES);
    let key_pair = get_random_test_key_pair();
    let header = Some(TEST_HEADER.as_ref());
    let ph = Some(TEST_PRESENTATION_HEADER_1.as_ref());
    let generators = create_generators_helper(messages.len());
    let first_and_last_indices_revealed =
        &[0, NUM_MESSAGES - 1].iter().cloned().collect();

    let signature = Signature::new(
        &key_pair.secret_key,
        &key_pair.public_key,
        header,
        &generators,
        &messages,
    )
    .expect("signing failed");
    assert_eq!(
        signature
            .verify(&key_pair.public_key, header, &generators, &messages)
            .expect("verification failed"),
        true
    );

    let (proof, revealed_messages) = test_helper::proof_gen(
        &key_pair.public_key,
        &signature,
        header,
        ph,
        &generators,
        &messages,
        first_and_last_indices_revealed,
        &mut OsRng,
    );
    assert_eq!(
        proof
            .verify(
                &key_pair.public_key,
                header,
                ph,
                &generators,
                &revealed_messages
            )
            .expect("proof verification failed"),
        true
    );

    let proof_octets = proof.to_octets();
    let proof_deserialized = Proof::from_octets(&proof_octets)
        .expect("roundtrip proof deserialization failed");
    assert_eq!(
        proof, proof_deserialized,
        "proof from roundtrip deserialization doesn't match to original proof"
    );

    assert_eq!(
        proof_deserialized
            .verify(
                &key_pair.public_key,
                header,
                ph,
                &generators,
                &revealed_messages
            )
            .expect("roundtrip deserialized proof verification failed"),
        true
    );
}

#[test]
fn gen_verify_different_key_pairs() {
    use rand::SeedableRng;
    let mut rng = MockRng::from_seed([1u8; 16]);

    let header = Some(TEST_HEADER.as_ref());
    let ph = Some(TEST_PRESENTATION_HEADER_1.as_ref());
    let messages = get_test_messages();
    let generators = create_generators_helper(messages.len());

    let expected_proofs = [
            ["", "", "", "", "", ""],
            ["", "", "", "", "", ""],
            ["", "", "", "", "", ""],
            ["", "", "", "", "", ""],
            ["", "", "", "", "", ""],
            ["", "", "", "", "", ""],
            ["", "", "", "", "", ""],
        ];

    for i in 0..TEST_KEY_INFOS.len() {
        let pk = PublicKey::from(
            &SecretKey::new(
                TEST_KEY_GEN_IKM.as_ref(),
                TEST_KEY_INFOS[i].as_ref(),
            )
            .expect("secret key generation failed"),
        );

        // start with all hidden messages
        let mut proof_msgs: Vec<ProofMessage> =
            messages.iter().map(|a| ProofMessage::Hidden(*a)).collect();

        let signature = Signature::from_octets(
            &<[u8; Signature::SIZE_BYTES]>::try_from(
                hex::decode(EXPECTED_SIGNATURES[i])
                    .expect("hex decoding failed"),
            )
            .expect("data conversion failed"),
        )
        .expect("signature deserialization failed");
        assert_eq!(
            signature
                .verify(&pk, Some(&TEST_HEADER), &generators, &messages)
                .unwrap(),
            true
        );

        let mut proof_values: Vec<String> = Vec::new();

        // Reveal 1 message at a time
        for j in 0..proof_msgs.len() {
            let proof = Proof::new_with_rng(
                &pk,
                &signature,
                header,
                ph,
                &generators,
                proof_msgs.as_slice(),
                &mut rng,
            )
            .expect("proof generation failed");

            let expected_proof = hex::decode(expected_proofs[i][j]).expect(
                "expected
             proof test data decoding failed",
            );
            assert_eq!(proof.to_octets(), expected_proof);
            proof_values.push(hex::encode(proof.to_octets()));
            let mut revealed_msgs = Vec::new();
            for k in 0..j {
                revealed_msgs.push((k as usize, proof_msgs[k].get_message()));
            }

            assert_eq!(
                proof
                    .verify(&pk, header, ph, &generators, &revealed_msgs)
                    .expect("proof verification failed"),
                true
            );
            proof_msgs[j] = ProofMessage::Revealed(messages[j]);
        }
        // println!("{:?},", proof_values);
    }
}

// Test generated proof uniqueness although every proof-gen API call uses Rng.
#[test]
fn proof_uniqueness() {
    const NUM_MESSAGES: usize = 5;
    let key_pair = get_random_test_key_pair();
    let key_pair2 = get_random_test_key_pair();
    let header = Some(TEST_HEADER.as_ref());
    let header2 = Some(ANOTHER_TEST_HEADER.as_ref());
    let ph = Some(TEST_PRESENTATION_HEADER_1.as_ref());
    let ph2 = Some(TEST_PRESENTATION_HEADER_2.as_ref());
    let messages = get_random_test_messages(NUM_MESSAGES);
    let messages2 = get_random_test_messages(NUM_MESSAGES);
    let generators = &create_generators_helper(messages.len());
    let generators_different_message_gens_seed = &Generators::new(
        GLOBAL_BLIND_VALUE_GENERATOR_SEED,
        GLOBAL_BLIND_VALUE_GENERATOR_SEED,
        b"test-message-generators-seed-2".as_ref(),
        messages.len(),
    )
    .expect(
        "generators creation with different message generators seed failed",
    );
    let indices: Vec<usize> = (0..NUM_MESSAGES).collect();
    let indices_all_hidden = &HashSet::<usize>::new();
    let indices_all_revealed = &indices.iter().cloned().collect();
    let first_and_last_indices_revealed =
        &[0, NUM_MESSAGES - 1].iter().cloned().collect();
    let signature = Signature::new(
        &key_pair.secret_key,
        &key_pair.public_key,
        header,
        generators,
        messages.clone(),
    )
    .expect("signing failed");
    let signature_with_different_key_pair = Signature::new(
        &key_pair2.secret_key,
        &key_pair2.public_key,
        header,
        generators,
        messages.clone(),
    )
    .expect("signing failed");

    // The test data for a pairs of proofs generation, values vary in a single
    // input parameter of `Proof::new(..)` which has 6 input paramters.
    let test_data = [
        (
            (
                &key_pair.public_key,
                &signature,
                header,
                ph,
                generators,
                messages.clone(),
                indices_all_hidden,
            ),
            (
                &key_pair2.public_key,
                &signature,
                header,
                ph,
                generators,
                &messages,
                indices_all_hidden,
            ),
            "public keys differ",
        ),
        (
            (
                &key_pair.public_key,
                &signature,
                header,
                ph,
                generators,
                messages.clone(),
                indices_all_hidden,
            ),
            (
                &key_pair.public_key,
                &signature_with_different_key_pair,
                header,
                ph,
                generators,
                &messages,
                indices_all_hidden,
            ),
            "signatures differ, generated with different key-pair",
        ),
        (
            (
                &key_pair.public_key,
                &signature,
                header,
                ph,
                generators,
                messages.clone(),
                indices_all_hidden,
            ),
            (
                &key_pair.public_key,
                &signature,
                header2,
                ph,
                generators,
                &messages,
                indices_all_hidden,
            ),
            "headers differ",
        ),
        (
            (
                &key_pair.public_key,
                &signature,
                header,
                ph,
                generators,
                messages.clone(),
                indices_all_hidden,
            ),
            (
                &key_pair.public_key,
                &signature,
                header,
                ph2,
                generators,
                &messages,
                indices_all_hidden,
            ),
            "presentation headers differ",
        ),
        (
            (
                &key_pair.public_key,
                &signature,
                header,
                ph,
                generators,
                messages.clone(),
                indices_all_hidden,
            ),
            (
                &key_pair.public_key,
                &signature,
                header,
                ph,
                generators_different_message_gens_seed,
                &messages,
                indices_all_hidden,
            ),
            "message-generators differ",
        ),
        (
            (
                &key_pair.public_key,
                &signature,
                header,
                ph,
                generators,
                messages.clone(),
                indices_all_hidden,
            ),
            (
                &key_pair.public_key,
                &signature,
                header,
                ph,
                generators,
                &messages2,
                indices_all_hidden,
            ),
            "messages differ",
        ),
        (
            (
                &key_pair.public_key,
                &signature,
                header,
                ph,
                generators,
                messages.clone(),
                indices_all_hidden,
            ),
            (
                &key_pair.public_key,
                &signature,
                header,
                ph2,
                generators,
                &messages2,
                indices_all_revealed,
            ),
            "revealed indices differ - all hidden vs all revealed",
        ),
        (
            (
                &key_pair.public_key,
                &signature,
                header,
                ph,
                generators,
                messages.clone(),
                indices_all_hidden,
            ),
            (
                &key_pair.public_key,
                &signature,
                header,
                ph2,
                generators,
                &messages2,
                first_and_last_indices_revealed,
            ),
            "revealed indices differ, all hidden vs first and last revealed",
        ),
        (
            (
                &key_pair.public_key,
                &signature,
                header,
                ph,
                generators,
                messages.clone(),
                indices_all_revealed,
            ),
            (
                &key_pair.public_key,
                &signature,
                header,
                ph2,
                generators,
                &messages2,
                first_and_last_indices_revealed,
            ),
            "revealed indices differ, all revealed vs first and last revealed",
        ),
    ];

    for (
        (pk1, sig1, h1, ph1, gen1, msg1, revealed_indices1),
        (pk2, sig2, h2, ph2, gen2, msg2, revealed_indices2),
        failure_debug_message,
    ) in test_data
    {
        let (proof1, _) = test_helper::proof_gen_with_mock_rng(
            pk1,
            sig1,
            h1,
            ph1,
            gen1,
            &msg1,
            revealed_indices1,
        );
        let (proof2, _) = test_helper::proof_gen_with_mock_rng(
            pk2,
            sig2,
            h2,
            ph2,
            gen2,
            &msg2,
            revealed_indices2,
        );

        assert_ne!(
            proof1, proof2,
            "proofs should not be equal - {}",
            failure_debug_message
        );

        let (proof1, _) = test_helper::proof_gen(
            pk1,
            sig1,
            h1,
            ph1,
            gen1,
            &msg1,
            revealed_indices1,
            &mut OsRng,
        );
        let (proof2, _) = test_helper::proof_gen(
            pk2,
            sig2,
            h2,
            ph2,
            gen2,
            &msg2,
            revealed_indices2,
            &mut OsRng,
        );

        assert_ne!(
            proof1, proof2,
            "(OsRng case) proofs should not be equal - {}",
            failure_debug_message
        );
    }
}
